import "core-js/modules/es.array.push.js";
import { mapActions, mapState } from "vuex";
export default {
  name: "Cart",
  props: {
    cart: Array
  },
  data() {
    return {
      productsInCart: [],
      productIDs: [],
      currCart: null
    };
  },
  computed: {
    ...mapState(["token", "products"])
  },
  created() {
    this.currCart = this.cart;
  },
  methods: {
    ...mapActions(["getToken", "getCart", "removeGameFromCart", "getProduct"])
  },
  mounted() {
    this.currCart.forEach(el => {
      this.productIDs.push(el.productID);
    });
    this.productIDs.forEach(el2 => {
      this.products.forEach(prod => {
        if (el2 == prod.id) {
          this.productsInCart.push(prod);
        }
      });
    });
  }
};
