import { mapActions, mapState } from "vuex";
export default {
  name: "Cart",
  props: {
    cart: Array
  },
  data() {
    return {
      productsInCart: [],
      productIDs: [],
      currCart: null
    };
  },
  computed: {
    ...mapState(["token", "products"])
  },
  created() {
