import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    products: [],
    productTypes: [],
  },
  mutations: {
    addProducts(state, product) {
      state.products = product;
    },

    addProductTypes(state, productType) {
      state.productTypes = productType;
    },
  },
  actions: {
    fetchProducts({ commit }) {
      fetch("http://localhost:8000/admin/products/", {
        method: "GET",
      })
        .then((obj) => obj.json())
        .then((res) => {
          commit("addProducts", res.data);
        });
    },

    fetchProductTypes({ commit }) {
      fetch("http://localhost:8000/admin/productTypes/", {
        method: "GET",
      })
        .then((obj) => obj.json())
        .then((res) => {
          commit("addProductTypes", res.data);
        });
    },

    getProduct({ commit, state }, id) {
      return new Promise((resolve, reject) => {
        const item = state.items.filter((item) => item.objectID == id)[0];

        if (item) {
          resolve(item);
        } else {
          fetch(
            `https://collectionapi.metmuseum.org/public/collection/v1/objects/${id}`
          )
            .then((obj) => obj.json())
            .then((res) => {
              commit("addItem", res);
              resolve(res);
            });
        }
      });
    },
  },
});
