import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    products: [],
    productTypes: [],
  },
  mutations: {
    addProducts(state, product) {
      state.products = product;
    },

    addProductTypes(state, productType) {
      state.productTypes = productType;
    },
  },
  actions: {
    fetchProducts({ commit }) {
      fetch("http://localhost:8000/admin/products/", {
        method: "GET",
      })
        .then((obj) => obj.json())
        .then((res) => {
          commit("addProducts", res.data);
        });
    },

    fetchProductTypes({ commit }) {
      fetch("http://localhost:8000/admin/productTypes/", {
        method: "GET",
      })
        .then((obj) => obj.json())
        .then((res) => {
          commit("addProductTypes", res.data);
        });
    },
    getCategory({ commit, state }, id) {
      return new Promise((resolve, reject) => {
        const c = state.categories.filter((ctg) => ctg.id == id)[0];
        if (c) {
          resolve(c);
        } else {
          fetch(`http://127.0.0.1:8090/admin/categories/${id}`)
            .then((obj) => obj.json())
            .then((data) => {
              if (data.msg) {
                alert(data.msg);
                return;
              }
              fetch(`http://127.0.0.1:8090/admin/games/category/${data.id}`)
                .then((res) => res.json())
                .then((data2) => {
                  data["games"] = data2;
                });
              commit("addCategory", data);
              resolve(data);
            });
        }
      });
    },
  },
});
