import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    products: [],
    productTypes: [],
  },
  mutations: {
    addProduct(state, product) {
      state.products.push(product);
    },

    addProductType(state, productType) {
      state.productTypes = productType;
    },
  },
  actions: {},
});
